import React, {Component}  from 'react';
import './Sign.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';

//const sign = (props) => {
class Sign extends Component {
    constructor() {
    super();
    this.state = {
            email: '',
            name: '',
            password: '',
            confirmPassword: ''
        }
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleConfirmPasswordChange = this.handleConfirmPasswordChange.bind(this);
    }

handleEmailChange(event){
    this.setState({ email: event.target.value })
    console.log(this.state.email)
}

handleNameChange(event){
    this.setState({ name: event.target.value })

}

handlePasswordChange(event){
    this.setState({ password: event.target.value })
}

handleConfirmPasswordChange(event){
    this.setState({ confirmPassword: event.target.value })
}

handleFormSubmit = () => {
    fetch('/api/create', {
        method: 'POST',
        body: JSON.stringify
    })
}
render() {
return (
    <div className="SignUp"> 
    <Form>

<Form.Group controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" placeholder="Enter email" value = { this.state.email} onChange={ this.handleEmailChange } />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>
  </Form.Group>

  <Form.Group>
  <Form.Label>Name</Form.Label>
<Form.Control type="text" placeholder="Enter Name" value = { this.state.name} onChange={ this.handleNameChange } />
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Enter Password" value = { this.state.password} onChange={ this.handlePasswordChange } />
  </Form.Group>

    <Form.Group controlId="formBasicPassword">
    <Form.Label>Confirm Password</Form.Label>
    <Form.Control type="password" placeholder="Confirm Password" value = { this.state.confirmPassword} onChange={ this.handleConfirmPasswordChange } />
  </Form.Group>


  <Button variant="primary" type="submit">
    Submit
  </Button>

</Form>        
    </div>
)}}

export default Sign;
