import React, { Component } from 'react';
import './Add.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import Table from 'react-bootstrap/Table';

class Add extends Component {
    constructor() {
    super();
    this.state = {
        nameOfHabit:  null,
        timesPerDay: null

    }
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleNameOfHabit = this.handleNameOfHabit.bind(this);
    this.handleTimesPerDay = this.handleTimesPerDay.bind(this);
    }

    handleFormSubmit(event){
        console.log("Submit Form");
    }

    handleNameOfHabit(event){
    this.setState({ nameOfHabit: event.target.value })

    }

    handleTimesPerDay(event){
    this.setState({ timesPerDay: event.target.value })

    }

    render() {

    return (
    <div className= "Add">
    <header className="Add-header">
    <Form onSubmit={this.handleFormSubmit}>  
    <Table striped bordered hover variant="dark">
    <thead>
        <tr>
        <th>
            Enter Name of the Habit
        </th> 
        <th> 
           <Form.Group controlId="exampleForm.ControlSelect1">
                <Form.Label>How many Times a day would the Habit be done</Form.Label>
                <Form.Control as="select" >
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                    <option>6</option>
                </Form.Control>
           </Form.Group>
        </th> 
        <th>
        <text> CLOCK HERE </text> 
        </th>
        </tr>
    <tbody>
        <tr>
        <td>

        </td>
        <td> 
            <Form.Group>                                                                                                 
                <Form.Control type="text" placeholder="Enter Name of the Habit" 
                required value = { this.state.nameOfHabit} 
                onChange={ this.handleNameOfHabit }  />  
            </Form.Group>
        </td>

        <td> 

        </td>
        </tr> 

    </tbody>
    </thead>
    </Table>
    </Form>
    </header>
    </div>
    );
    }
    }

export default Add;
