import React, { Component } from 'react';
import Add from '../Add/Add';
import Habit from '../Habit/Habit';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

class LandPage extends Component {
    constructor() {
    super();
    this.state = {
        email: undefined,       
        list: []
    }
        this.setEmail = this.setEmail.bind(this)
    }
    updateList(list){
        this.setState({
            list: list
        })
    }
    setEmail(newEmail){
        this.setState({
            email:newEmail
        })
        this.props.setEmail(this.state.email)
        console.log("LandPage", this.state.email)
    }


handlePopulate(data, i) {
    return (
    <Table striped bordered hover variant="dark" key={i}>
    <thead>
        <tr>
        <th>
    {data.nameOfHabit[i]}
     </th>
        </tr>
    </thead>
    <tbody>
        <tr>
        <td>
    {data.timesPerDay[i]}
    </td>
        <td>
    {data.Total[i]}
    </td>
        <td>
        <Button variant="primary"i value={i} type="submit" onClick={event => this.handleAddTotal(event, "value")}>
            Add One
        </Button>
        </td>
        </tr>
    </tbody>
    </Table>
    )
    }


    render() { 
    return (
    <div className="Habit">
    <header className="Habit-header">
        <Add email = {this.props.email} list = { this.state.list } />
        <Habit email = {this.props.email} handleLoad= { this.handleLoad} list = { this.state.list } /> 
    </header>
    </div>
    );
}
}
export default LandPage;
