import React, { Component } from 'react';
import './Habit.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

class Habit extends Component {
    constructor(props) {
    super();
    this.state = {
        email: undefined,
        list: undefined
    }
    this.handleLoad = this.handleLoad.bind(this)
    this.handlePopulate = this.handlePopulate.bind(this)
    this.handleAddTotal = this.handleAddTotal.bind(this)
    }
//Handle function on Page Load
componentDidMount(){
    return this.handleLoad()
}

handleAddTotal(event) {
    console.log(event.target.value);
//    handleGetEntry(event.target.value) 
    console.log(this.state.email);
}

handleGetEntry(index){
    const data = {"email" : this.state.email, "index" : index};
    fetch('http://127.0.0.1:5000/api/getEntry', {
        crossDomain: true,
        mode: 'cors',
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        },  
        body: JSON.stringify(data),
        })  
        .then(response => response.json())
        .then(data => {
        console.log('Success', data);
})
}

handleLoad() {
    const data = { "email": this.props.email }
    fetch('http://127.0.0.1:5000/api/pullHabits', {
        crossDomain: true,
        mode: 'cors',
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        },
        body: JSON.stringify(data),
        })
        .then(response => response.json())
        .then(data => {
        console.log('Success', data);
        console.log(data['nameOfHabit'].length);
        let len = data['nameOfHabit'].length;
        var list = []
        for (var i = 0; i < len; i++){
            list.push(this.handlePopulate(data, i))

        }
        this.setState({
            list: list
        })
    })
}

handlePopulate(data, i) {
    return (
    <Table striped bordered hover variant="dark" key={i}>
    <thead>
        <tr>
        <th>
    {data.nameOfHabit[i]}
     </th> 
        </tr>
    </thead>
    <tbody>
        <tr> 
        <td>  
    {data.timesPerDay[i]}
    </td>
        <td>
    {data.Total[i]}
    </td> 
        <td>
        <Button variant="primary"i value={i} type="submit" onClick={event => this.handleAddTotal(event, "value")}>
            Add One
        </Button>   
        </td>
        </tr> 
    </tbody>
    </Table>
    )
    }

    render() {
    return (
    <div className="Habit">
    <header className="Habit-header">
    { this.state.list }
    </header>
    </div>

    );
    }
}

export default Habit;
